$(document).ready(function () {
    $("#slider").owlCarousel({ nav: !0, dots: !0, margin: 60, slideBy: 4, responsiveClass: !0, responsive: { 1024: { items: 4 }, 768: { items: 3, nav: !1 }, 0: { items: 1, center: !0, autoWidth: !0, nav: !1 } } }),
        $("#features ul li").on("click", function () {
            $("#features ul li").removeClass("active"), $(this).addClass("active"), $("#feature-images img, #feature-images > div").hide(), $("." + $(this).attr("data-tab")).show();
        }),
        $(function () {
            var e = window.location.href.slice(window.location.href.indexOf("currency"));
            $("#currency-selector > option").each(function () {
                this.value == e && (this.selected = "selected");
            }),
                $("." + $("#currency-selector").val()).show(),
                $("#currency-selector").on("change", function () {
                    $(".plan-currency").hide(), $("." + $(this).val()).show();
                });
            var a = $(".tabs").find("a"),
                n = $("#plan-size-selector");
            function t() {
                $(".plan-price").hide();
            }
            a.on("click", function (e) {
                e.preventDefault(), t(), a.parent().removeClass("active"), $(this).parent().addClass("active");
                var i = $(this).attr("data-plan");
                $("." + i).show(), n.val(i);
            }),
                n.on("change", function () {
                    t();
                    var e = $(this).val();
                    $("." + e).show(),
                        a.parent().removeClass("active"),
                        a.each(function () {
                            $(this).attr("data-plan") === e && $(this).parent().addClass("active");
                        });
                });
        }),
        $(".faq").on("click", "button", function () {
            var e = $(this).closest(".faq").find("dd");
            $(this).toggleClass("open"), e.slideToggle("fast");
        }),
        $(function () {
            -1 !== window.location.search.indexOf("chat") && Intercom("showNewMessage");
        }),
        $(".open-popup-link").magnificPopup({ type: "inline", midClick: !0 });

    window.addEventListener('click', function (e) {
        if ($('#dropdown-notification').length) {
            if ($('.dropdown-notification').is(":visible")) {
                if (document.getElementById('dropdown-notification').contains(e.target)) {
                } else {
                    $('.dropdown-notification').hide();
                }
            } else {
                if (document.getElementById('dropdown-notification').contains(e.target)) {
                    $('.dropdown-notification').show();

                    $.ajax({
                        url: '/read-notifications',
                        type: 'GET',
                    }).done(function (result) {
                    });
                }

                if (document.getElementById('notification').contains(e.target)) {
                    $('.dropdown-notification').show();

                    $.ajax({
                        url: '/read-notifications',
                        type: 'GET',
                    }).done(function (result) {
                    });
                }
            }
        }
    });
});


var openSearch = function () {
    var e = $("#search div"),
        a = $("#searchMenuForm");
    !1 === e.hasClass("open") &&
        (e.addClass("open"),
            e.find("input[type='search']").focus(),
            e.find("button").on("click", function () {
                a.submit();
            }));
};

var toggleMobileMenu = function () {
    var e = $("#menu");
    e.slideToggle("fast", function () {
        e.is(":visible") ? $("#toggle-menu").addClass("open") : $("#toggle-menu").removeClass("open");
    });
};


